CREATE TABLE bank (
    bank_id INT PRIMARY KEY,
    bank_name VARCHAR(100) NOT NULL,
    branch_name VARCHAR(100),
    address VARCHAR(255),
    city VARCHAR(100),
    state VARCHAR(100),
    ifsc_code VARCHAR(20),
    pin_code VARCHAR(10),
    contact_number VARCHAR(20),
    established_year INT,
    UNIQUE (city, ifsc_code)
    );

